import React from 'react';
import './App.css';
import Typography from '@material-ui/core/Typography';

function CovidNewsList(props) {
  const classes = useStyles();
  let jsonList = "";
  let json = "";
  let cardObj = "";
  let companyObjAry = [];

  jsonList = require("./json/2021-01/CompanyList.json");
  for (var i in jsonList){
    json = require("./json/2021-01/" + jsonList[i].company + ".json");
    cardObj = json.map((data, index) =>
      <Grid item xs={6} md={4}>
        <Link href={"2021-01/"+data.id}>
          <Card>
            <CardActionArea>
              <CardMedia
                component="img"
                alt={data.name}
                image={data.image.building}
                title={data.name}
                height="180"
              />
              <CardContent className="text-left">
                <div className="d-flex">
                  <div className="p-2">
                    <Typography gutterBottom variant="subtitle1" component="h2" className="font-weight-bold">
                      {ArrangeData(data.name)}
                    </Typography>
                    <Typography variant="body2" color="textSecondary" component="p">
                      {data.genre}
                    </Typography>
                  </div>
                </div>
              </CardContent>
            </CardActionArea>
          </Card>
        </Link>
      </Grid>
    );
    companyObjAry.push(cardObj);
  }

  return (
    <div className={classes.root}>
      <div className="container my-5 mx-auto">
        <Grid container spacing={2}>
          {companyObjAry}
        </Grid>
      </div>
    </div>
  );
}

export default CovidNewsList;
