import React, { Component } from 'react';
import { BarChart, Bar } from 'recharts';
import { XAxis, YAxis, CartesianGrid, Tooltip } from 'recharts'
import './App.css';

import Papa from 'papaparse';


const data = [
  {name: 'A', uv: 4000},
  {name: 'B', uv: 3000},
  {name: 'C', uv: 2000},
  {name: 'D', uv: 2780},
  {name: 'E', uv: 1890},
  {name: 'F', uv: 2390},
  {name: 'G', uv: 3490},
  {name: 'H', uv: 1490},
  {name: 'I', uv: 2950},
  {name: 'J', uv: 4690},
]

class App extends Component {

  constructor (props) {
		super(props);
		this.state = {
			csvfile: 'undefined'
		};
  }

  getCSV = () =>{
		return new Promise(resolve =>{
			var buf;
			const { csvfile } = this.state;
			Papa.parse(csvfile, {
				header: true,
				delimiter:',',
				complete:(buf) =>{
					console.log(buf.data);
				},
			});
		});
  }
	handleChange = event => {
		console.log(event.target.files[0]);
		this.setState({
      csvfile: event.target.files[0]
		});
    this.getCSV();
  }

  render() {
    return (
      <div>
    			<input type="file" onChange={this.handleChange} />
      <BarChart width={730} height={350} data={data}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="name" />
        <YAxis />
        <Tooltip />
        <Bar dataKey="uv" fill="#82ca9d" />
        <Bar dataKey="uv" fill="#784d88" />
      </BarChart>
      </div>
    );
  }
}

export default App;
