import React, { useState, useEffect } from 'react';
import axios from "axios";

function CovidNewsList() {
  const [posts, setPosts] = useState(0);
  useEffect(() => {
    axios.get("https://www.daily-tohoku.news/wp-json/wp/v2/posts?categories=30&per_page=100&page=1").then(response => {
      const posts = response.data;
      setPosts(posts);
    });
  });
  return (
    <div>
        {posts} 
    </div>
  );
}
