import React, { useState, useEffect } from 'react';
import axios from "axios";

function CovidNewsList() {
  const [data, setData] = useState([]);
  useEffect(async () => {
    const result = await axios(
      'https://www.daily-tohoku.news/wp-json/wp/v2/posts?categories=30&per_page=100&page=1',
    );
    setData(result.data);
  }, []);
  return (
    <div className="contWrapper">
      <Typography className="font-weight-bold my-3" variant="h5" component="h2">
        新型コロナ関連ニュース
      </Typography>
      <ul>
      {
        data.map(item => (
          <li>
            <a href={item.link}>{item.title.rendered}</a>
          </li>
        ))
      }
      </ul>
    </div>
  );
}
export default CovidNewsList;

