import React, { useState, useEffect } from 'react';
import axios from "axios";
import './App.css';

/*
**************************************************
FormatDate (date, format)
--------------------------------------------------
date: 日付オブジェクト
format: 書式フォーマット
--------------------------------------------------
jsonから取得したdateをフォーマットを指定して変換する。
**************************************************
*/
function FormatDate (date, format) {
  format = format.replace(/yyyy/g, date.getFullYear());
  format = format.replace(/MM/g, ('0' + (date.getMonth() + 1)).slice(-2));
  format = format.replace(/dd/g, ('0' + date.getDate()).slice(-2));
  format = format.replace(/HH/g, ('0' + date.getHours()).slice(-2));
  format = format.replace(/mm/g, ('0' + date.getMinutes()).slice(-2));
  format = format.replace(/ss/g, ('0' + date.getSeconds()).slice(-2));
  format = format.replace(/SSS/g, ('00' + date.getMilliseconds()).slice(-3));
  return format;
};

// 例：2017年1月2日3時4分5秒6ミリ秒
/*
var date = new Date(2017, 0, 2, 3, 4, 5, 6);

console.log(FormatDate(date, 'yyyyMMdd')); // "20170102"
console.log(FormatDate(date, 'yyyyMMddHHmmssSSS')); // "20170102030405006"
console.log(FormatDate(date, 'yyyy/MM/dd')); // "2017/01/02"
console.log(FormatDate(date, 'yyyy-MM-dd')); // "2017-01-02"
console.log(FormatDate(date, 'HH:mm')); // "03:04"
console.log(FormatDate(date, 'HH:mm:ss:SSS')); // "03:04:05:006"
*/

function CovidNewsList() {
  const [data, setData] = useState([]);
  useEffect(async () => {
    const result = await axios(
      'https://www.daily-tohoku.news/wp-json/wp/v2/posts?categories=30&per_page=100&page=1',
    );
    setData(result.data);
  }, []);
  return (
    <div className="contWrapper w-100 py-3">
      <div className="contTitle">
        新型コロナ関連ニュース
      </div>
      <div className="contBody of-y-scrl p-3">
        <div className="mh-250px p-3">
          <ul className="fs90 ls-none p-0">
          {
            data.map(item => (
              <li className="my-2">
                <span className="mr-3">{FormatDate(new Date(item.date), 'yyyy/MM/dd')}</span><a href={item.link}>{item.title.rendered.replace(/<span class="d-inlne bg-dark rounded px-2 py-1 text-white m-1 fs90">Free<\/span>/, '')}</a>
              </li>
            ))
          }
          </ul>
        </div>
      </div>
    </div>
  );
}
export default CovidNewsList;

