import React, { PureComponent } from 'react';
import { BarChart, Bar,  XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';

const data = [
  {
    name: 'A',
    uv: 4000,
    pv: 2400,
    amt: 2400,
  },
  {
    name: 'B',
    uv: 3000,
    pv: 1398,
    amt: 2210,
  },
  {
    name: 'C',
    uv: 2000,
    pv: 9800,
    amt: 2290,
  },
  {
    name: 'D',
    uv: 2780,
    pv: 3908,
    amt: 2000,
  },
  {
    name: 'E',
    uv: 1890,
    pv: 4800,
    amt: 2181,
  },
  {
    name: 'F',
    uv: 2390,
    pv: 3800,
    amt: 2500,
  },
  {
    name: 'G',
    uv: 3490,
    pv: 4300,
    amt: 2100,
  },
  {
    name: 'H',
    uv: 3490,
    pv: 4300,
    amt: 2100,
  },
  {
    name: 'I',
    uv: 3490,
    pv: 4300,
    amt: 2100,
  },
  {
    name: 'J',
    uv: 3490,
    pv: 4300,
    amt: 2100,
  },
];



export default class Example extends PureComponent {
  render() {

    function CSVToMatrix(csv,delimiter){
      let matrix = [];
      csv.split('\n').map( l => { l.trim() == "" ? 0 : matrix.push(l.trim().split(delimiter).map(v=>v.trim())) });
      return matrix;
    }

    function MatrixToJSON(matrix,from,to){
      let jsonResult = []; from = from||0;  
      matrix.map((a,i) => {
        let obj = Object.assign({}, ...matrix[0].map((h, index) => ({[h]: matrix[i][index]})));
        jsonResult.push(obj);
      })
      return to ? jsonResult.splice(from,to) : jsonResult.splice(from)
    }
    var inputCSV = "/data/03.csv"; 
    var MATRIX = CSVToMatrix(inputCSV, ',');
    var JSON = MatrixToJSON( MATRIX, 1);

    return (
      <div>
        <BarChart
          width={500}
          height={300}
          data={data}
          margin={{
            top: 20,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="pv" stackId="a" fill="#8884d8" />
          <Bar dataKey="uv" stackId="a" fill="#82ca9d" />
        </BarChart>
      </div>
    );
  }
}
