import React, { useState, useEffect } from 'react';
import axios from "axios";
import './App.css';
import Typography from '@material-ui/core/Typography';

function formatDate (date, format) {
  format = format.replace(/yyyy/g, date.getFullYear());
  format = format.replace(/M/g, (date.getMonth() + 1));
  format = format.replace(/d/g, (date.getDate()));
  format = format.replace(/H/g, (date.getHours()));
  format = format.replace(/m/g, (date.getMinutes()));
  format = format.replace(/s/g, (date.getSeconds()));
  format = format.replace(/S/g, (date.getMilliseconds()));
  return format;
};
// 2017年1月2日3時4分5秒6ミリ秒
var date = new Date(2017, 0, 2, 3, 4, 5, 6);

console.log(formatDate(date, 'yyyy/M/d')); // "2017/1/2"
console.log(formatDate(date, 'yyyy-M-d')); // "2017-1-2"
console.log(formatDate(date, 'H:m')); // "3:4"
console.log(formatDate(date, 'H:m:s:S')); // "3:4:5:6"

function CovidNewsList() {
  const [data, setData] = useState([]);
  useEffect(async () => {
    const result = await axios(
      'https://www.daily-tohoku.news/wp-json/wp/v2/posts?categories=30&per_page=100&page=1',
    );
    setData(result.data);
  }, []);
  return (
    <div className="contWrapper">
      <Typography className="font-weight-bold my-3" variant="h5" component="h2">
        新型コロナ関連ニュース
      </Typography>
      <div className="contBody">
        <ul className="fs90">
        {
          data.map(item => (
            <li>
              <span>{formatDate(new Date(item.date), 'yyyy/M/d')}</span><a href={item.link}>{item.title.rendered.replace(/<span class="d-inlne bg-dark rounded px-2 py-1 text-white m-1 fs90">Free<\/span>/, '')}</a>
            </li>
          ))
        }
        </ul>
      </div>
    </div>
  );
}
export default CovidNewsList;

