import React from 'react';
import './App.css';
import Typography from '@material-ui/core/Typography';

const tagetFile = './data/02.csv';

var getCsvData = (dataPath) => {
  const request = new XMLHttpRequest();
  request.open("GET", dataPath, false);
	request.send(null);
  return request.responseText;
};

var csvToJson = (csvStr, userOptions) => {
  if (typeof csvStr !== 'string') {
    return null
  };
  var options = { header : 0, columnName : [], ignoreBlankLine : true };
  if (userOptions) {
    if (userOptions.header) options.header = userOptions.header;
    if (userOptions.columnName) options.columnName = userOptions.columnName;
  }
  var rows = csvStr.split('\n');
  var json = [], line = [], row = '', data = {};
  var i, len, j, len2;
  for (i = 0, len = rows.length; i < len; i++) {
    if ((i + 1) <= options.header) continue;
    if (options.ignoreBlankLine && rows[i] === '') continue;
    line = rows[i].split(',');
    /*
    ["2020年4月7日", "18", "1", "17"]
    0（検査日時）: "2020年4月7日"
    1（実施数）: "18"
    2（陽性者数）: "1"
    3（陰性者数）: "17"
    */
    if (options.columnName.length > 0) {
      if(!line[0]) {
        row = null;
        continue;
      }else {
        if(!line[2]) {
          row = null;
          continue;
        }
      }
      data = {};
      for (j = 0, len2 = options.columnName.length; j < len2; j++) {
        if (typeof line[j] !== 'undefined') {
          row = line[j];
          row = row.replace(/^"(.+)?"$/, '$1');
          row = row.replace(/\r/, '');
          row = row.replace(/\n/, '');
        } else {
          row = null;
        }
        data[options.columnName[j]] = row;
      }
      json.unshift(data);
    } else {
      json.unshift(line);
    }
  }
  return json;
};

var jsonObj = csvToJson(getCsvData(tagetFile), { header : 1, columnName : ['ＮＯ', '全国地方公共団体コード', '都道府県名', '保健所管内' ,'公表_年月日', '判明_年月日', '居住地', '年代', '性別'] });
console.log(jsonObj);

const CovidTestedStatus = (props) => {
  return(
    <div className="contWrapper">
      <Typography className="font-weight-bold my-3" variant="h5" component="h2">
        感染症患者データ
      </Typography>
      <div class="table-responsive">
        <table id="patient-table" className="table table-striped table-bordered text-nowrap fs80">
          <thead>
            <tr>
              <th>公表_年月日</th>
              <th>居住地</th>
              <th>年代</th>
              <th>性別</th>
            </tr>
          </thead>
          <tbody>
          {
            jsonObj.map(item => (
              <tr>
                <td className="text-center" key={("date-")+item['ＮＯ']}>{item['公表_年月日']}</td>
                <td className="text-left" key={("area-")+item['ＮＯ']}>{item['居住地']}</td>
                <td className="text-center" key={("age-")+item['ＮＯ']}>{item['年代']}</td>
                <td className="text-center" key={("gender-")+item['ＮＯ']}>{item['性別']}</td>
              </tr>
            ))
          }
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default CovidTestedStatus;
