import React from 'react';
import { BarChart, Bar,  XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';
import './App.css';
import exportFunc from './lib.js';

const tagetFile = './data/04.csv';

var jsonObj = exportFunc.CsvToJson(exportFunc.GetCsvData(tagetFile)[0], { header : 1, columnName : ['検査日時', '実施数', '陽性者数', '陰性者数'] });
var caption = exportFunc.CsvToCaption(exportFunc.GetCsvData(tagetFile)[0], { header : 1, columnName : [ '全国地方公共団体コード', '都道府県名', '受付_年月日', '相談件数'] });
var lastMod = exportFunc.FormatDate( exportFunc.GetCsvData(tagetFile)[1], 'yyyy/MM/dd' ) + '更新';

const CovidTestedNumber = (props) => {
  return(
    <div className="contWrapper w-100 py-4">
      <div className="contTitle">
        検査実施件数
      </div>
      <div className="contBody">
        <div className="of-y-scrl px-3">
          <div className="chartWrapper">
            <BarChart
              className="graphLTR"
              width={5120}
              height={320}
              data={jsonObj}
              margin={{
                top: 20,
                right: 30,
                left: 20,
                bottom: 5,
              }}
            >
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="検査日時" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="実施数" stackId="a" fill="#26A69A" />
            </BarChart>
          </div>
          <div className="graphCaption mb-3">
            {caption}
          </div>
          <div className="graphCaption mb-3">
            ※速報値として公開するものであり、後日確定データとして修正される場合があります。<br />
            ※陽性数は判明日の件数であるため、公表時の数値と異なる場合があります。<br />
            ※退院確認検査数は計上していません。<br />
            ※自費検査（行政検査以外の検査）は含みません。<br />
            ※2020年3月22日以前の検査実施数は94件です（すべて陰性）。<br />
          </div>
        </div>
        <div className="graphCaption text-right mt-3 px-3">
          <a href="https://opendata.pref.aomori.lg.jp/dataset/1531.html" className="covidLink" target="_blank">出典元：青い森オープンデータカタログ </a>
          <div className="mt-1">
            {lastMod}
          </div>
        </div>

      </div>
    </div>
  );
};

export default CovidTestedNumber;
