import React from 'react';
import { BarChart, Bar,  XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';
import './App.css';

const tagetFile = './data/05.csv';

var getCsvData = (dataPath) => {
  const request = new XMLHttpRequest();
  request.open("GET", dataPath, false);
	request.send(null);
  return request.responseText;
};

var csvToJson = (csvStr, userOptions) => {
  if (typeof csvStr !== 'string') {
    return null
  };
  var options = { header : 0, columnName : [], ignoreBlankLine : true };
  if (userOptions) {
    if (userOptions.header) options.header = userOptions.header;
    if (userOptions.columnName) options.columnName = userOptions.columnName;
  }
  var rows = csvStr.split('\n');
  var json = [], line = [], row = '', data = {};
  var i, len, j, len2;
  for (i = 0, len = rows.length; i < len; i++) {
    if ((i + 1) <= options.header) continue;
    if (options.ignoreBlankLine && rows[i] === '') continue;
    line = rows[i].split(',');
    /*
    ["2020年4月7日", "18", "1", "17"]
    0（検査日時）: "2020年4月7日"
    1（実施数）: "18"
    2（陽性者数）: "1"
    3（陰性者数）: "17"
    */
    if (options.columnName.length > 0) {
      if(!line[0]) {
        row = null;
        continue;
      }else {
        if(!line[2]) {
          row = null;
          continue;
        }
      }
      data = {};
      for (j = 0, len2 = options.columnName.length; j < len2; j++) {
        if (typeof line[j] !== 'undefined') {
          row = line[j];
          row = row.replace(/^"(.+)?"$/, '$1');
          row = row.replace(/\r/, '');
          row = row.replace(/\n/, '');
        } else {
          row = null;
        }
        data[options.columnName[j]] = row;
      }
      json.unshift(data);
    } else {
      json.unshift(line);
    }
  }
  return json;
};

var jsonObj = csvToJson(getCsvData(tagetFile), { header : 1, columnName : [ '全国地方公共団体コード', '都道府県名', '受付_年月日', '相談件数'] });

console.log(jsonObj);

const CovidAdviceCenter = (props) => {
  return(
    <div className="contWrapper w-100 my-3">
      <div className="contTitle">
        相談センター相談件数
      </div>
      <div className="contBody">
        <div className="chartWrapper">
          <BarChart
            className="graphLTR"
            width={5120}
            height={320}
            data={jsonObj}
            margin={{
              top: 20,
              right: 30,
              left: 20,
              bottom: 5,
            }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="受付_年月日" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="相談件数" stackId="a" fill="#8884d8" />
          </BarChart>
        </div>
      </div>
    </div>
  );
};

export default CovidAdviceCenter;
