import React from 'react';
import './App.css';

const tagetFile = './data/02.csv';

var getCsvData = (dataPath) => {
  const request = new XMLHttpRequest();
  request.open("GET", dataPath, false);
	request.send(null);
  return request.responseText;
};

var csvToJson = (csvStr, userOptions) => {
  if (typeof csvStr !== 'string') {
    return null
  };
  var options = { header : 0, columnName : [], ignoreBlankLine : true };
  if (userOptions) {
    if (userOptions.header) options.header = userOptions.header;
    if (userOptions.columnName) options.columnName = userOptions.columnName;
  }
  var rows = csvStr.split('\n');
  var json = [], line = [], row = '', data = {};
  var i, len, j, len2;
  for (i = 0, len = rows.length; i < len; i++) {
    if ((i + 1) <= options.header) continue;
    if (options.ignoreBlankLine && rows[i] === '') continue;
    line = rows[i].split(',');
    /*
    ["2020年4月7日", "18", "1", "17"]
    0（検査日時）: "2020年4月7日"
    1（実施数）: "18"
    2（陽性者数）: "1"
    3（陰性者数）: "17"
    */
    if (options.columnName.length > 0) {
      if(!line[0]) {
        row = null;
        continue;
      }else {
        if(!line[2]) {
          row = null;
          continue;
        }
      }
      data = {};
      for (j = 0, len2 = options.columnName.length; j < len2; j++) {
        if (typeof line[j] !== 'undefined') {
          row = line[j];
          row = row.replace(/^"(.+)?"$/, '$1');
          row = row.replace(/\r/, '');
          row = row.replace(/\n/, '');
          row = row.replace(/[Ａ-Ｚａ-ｚ０-９]/g, function(s) {
            return String.fromCharCode(s.charCodeAt(0) - 0xFEE0);
          });
        } else {
          row = null;
        }
        data[options.columnName[j]] = row;
      }
      json.unshift(data);
    } else {
      json.unshift(line);
    }
  }
  return json;
};

var jsonObj = csvToJson(getCsvData(tagetFile), { header : 1, columnName : ['ＮＯ', '全国地方公共団体コード', '都道府県名', '保健所管内' ,'公表_年月日', '判明_年月日', '居住地', '年代', '性別'] });

{/* 降順 */}
function compareDown(a, b) {
  return parseInt(b['ＮＯ']) - parseInt(a['ＮＯ']);
}
{/* 昇順 */}
function compareUp(a, b) {
  return parseInt(a['ＮＯ']) - parseInt(b['ＮＯ']);
}
jsonObj.sort(compareDown);

console.log(jsonObj);

const CovidPatientData = (props) => {
  return(
    <div className="contWrapper w-100 my-3">
      <div className="contTitle">
        感染症患者データ
      </div>
      <div className="contBody of-y-scrl">
        <div className="mh-300px px-3 py-4 p-md-5">
          <div className="table-responsive">
            <table id="patient-table" className="table table-striped table-bordered text-left text-nowrap fs80">
              <thead>
                <tr>
                  <th className="border-right-def tablesorter-header tablesorter-headerUnSorted">公表_年月日</th>
                  <th className="border-right-def tablesorter-header tablesorter-headerUnSorted">居住地</th>
                  <th className="border-right-def tablesorter-header tablesorter-headerUnSorted">年代</th>
                  <th className="tablesorter-header tablesorter-headerUnSorted">性別</th>
                </tr>
              </thead>
              <tbody>
              {
                jsonObj.map(item => (
                  <tr>
                    <td className="border-right-def" key={("date-")+item['ＮＯ']}>{item['公表_年月日']}</td>
                    <td className="border-right-def" key={("area-")+item['ＮＯ']}>{item['居住地']}</td>
                    <td className="border-right-def" key={("age-")+item['ＮＯ']}>{item['年代']}</td>
                    <td key={("gender-")+item['ＮＯ']}>{item['性別']}</td>
                  </tr>
                ))
              }
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CovidPatientData;
