import React from "react";
import ReactModernDrawer from 'react-modern-drawer';
import 'react-modern-drawer/dist/index.css'
import MenuIcon from '@material-ui/icons/Menu';
import SideMenu from './side-menu';


const Drawer = () => {

  const [isOpen, setIsOpen] = React.useState(false)
  const toggleDrawer = () => {
    setIsOpen((prevState) => !prevState)
  }

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center text-nowrap">
        {/*
        <img src="./images/covid01.png" className="header-img" />
        */}
        <MenuIcon className="menu-icon" onClick={toggleDrawer} />
      </div>
      <ReactModernDrawer open={isOpen} onClose={toggleDrawer} direction='right'>
        <SideMenu />
      </ReactModernDrawer>
    </div>
  );
};

export default Drawer;
