import React from 'react';
import './App.css';
import Typography from '@material-ui/core/Typography';

const tagetFile = './data/03.csv';

var getCsvData = (dataPath) => {
  const request = new XMLHttpRequest();
  request.open("GET", dataPath, false);
	request.send(null);
  return request.responseText;
};

var csvToJson = (csvStr, userOptions) => {
  if (typeof csvStr !== 'string') {
    return null
  };
  var options = { header : 0, columnName : [], ignoreBlankLine : true };
  if (userOptions) {
    if (userOptions.header) options.header = userOptions.header;
    if (userOptions.columnName) options.columnName = userOptions.columnName;
  }
  var rows = csvStr.split('\n');
  var json = [], line = [], row = '', data = {};
  var i, len, j, len2;
  for (i = 0, len = rows.length; i < len; i++) {
    if ((i + 1) <= options.header) continue;
    if (options.ignoreBlankLine && rows[i] === '') continue;
    line = rows[i].split(',');
    /*
    ["2020年4月7日", "18", "1", "17"]
    0（検査日時）: "2020年4月7日"
    1（実施数）: "18"
    2（陽性者数）: "1"
    3（陰性者数）: "17"
    */
    if (options.columnName.length > 0) {
      if(!line[0]) {
        row = null;
        continue;
      }else {
        if(!line[2]) {
          row = null;
          continue;
        }
      }
      data = {};
      for (j = 0, len2 = options.columnName.length; j < len2; j++) {
        if (typeof line[j] !== 'undefined') {
          row = line[j];
          row = row.replace(/^"(.+)?"$/, '$1');
          row = row.replace(/\r/, '');
          row = row.replace(/\n/, '');
        } else {
          row = null;
        }
        data[options.columnName[j]] = row;
      }
      json.unshift(data);
    } else {
      json.unshift(line);
    }
  }
  return json;
};

var jsonObj = csvToJson(getCsvData(tagetFile), { header : 1, columnName : ['blank', '入院中', '（うち重症）', '入院調整中', '宿泊療養中', '自宅療養中', '退院・療養解除', '死亡', '総計'] });

const CovidCarrierStatus = (props) => {
  return(
    <div className="contWrapper w-100 p-3">
      <Typography className="font-weight-bold my-3" variant="body1" component="h2">
        検査陽性者の状況
      </Typography>
      <div className="contBody">
        <div className="w-100 mh-250px of-y-scrl">
          {
            jsonObj.map(item => (          
            <ul class="covid-graph">
                <li>
                <div class="d-flex justify-content-between px-2 py-1"><div class="p-2 text-nowrap">陽性者数 (累計)</div><div class="p-2 text-nowrap"><span> {item['総計']}</span> 人</div></div> 
                <ul class="pl-4">
                <li>
                    <div class="d-flex justify-content-between px-2 py-1"><div class="px-2 py-1 text-nowrap">入院中</div><div class="px-2 py-1 text-nowrap"><span> {item['入院中']}</span> 人</div></div> 
                    <ul class="pl-4">
                    <li>
                        <div class="d-flex justify-content-between px-2 py-1"><div class="px-2 py-1 text-nowrap">重症</div><div class="px-2 py-1 text-nowrap"><span> {item['（うち重症）']}</span> 人</div></div> 
                    </li>
                    <li>
                        <div class="d-flex justify-content-between px-2 py-1"><div class="px-2 py-1 text-nowrap">重症以外</div><div class="px-2 py-1 text-nowrap"><span> {parseInt(item['入院中'],10) - parseInt(item['（うち重症）'],10)}</span> 人</div></div> 
                    </li>
                    </ul>
                    </li>
                    <li>
                    <div class="d-flex justify-content-between px-2 py-1"><div class="px-2 py-1 text-nowrap">療養中</div><div class="px-2 py-1 text-nowrap"><span> {parseInt(item['宿泊療養中'],10) + parseInt(item['自宅療養中'],10)}</span> 人</div></div> 
                    <ul class="pl-4">
                        <li>
                        <div class="d-flex justify-content-between px-2 py-1"><div class="px-2 py-1 text-nowrap">宿泊療養中</div><div class="px-2 py-1 text-nowrap"><span> {item['宿泊療養中']} </span> 人</div></div> 
                        </li>
                        <li>
                        <div class="d-flex justify-content-between px-2 py-1"><div class="px-2 py-1 text-nowrap">自宅療養中</div><div class="px-2 py-1 text-nowrap"><span> {item['自宅療養中']} </span> 人</div></div> 
                        </li>
                    </ul>
                    </li>
                    <li>
                    <div class="d-flex justify-content-between px-2 py-1"><div class="px-2 py-1 text-nowrap">入院調整中</div><div class="px-2 py-1 text-nowrap"><span> {item['入院調整中']} </span> 人</div></div> 
                    </li>
                    <li>
                    <div class="d-flex justify-content-between px-2 py-1"><div class="px-2 py-1 text-nowrap">退院・療養解除</div><div class="px-2 py-1 text-nowrap"><span> {item['退院・療養解除']} </span> 人</div></div> 
                    </li>
                    <li>
                    <div class="d-flex justify-content-between px-2 py-1"><div class="px-2 py-1 text-nowrap">死亡</div><div class="px-2 py-1 text-nowrap"><span> {item['死亡']} </span> 人</div></div> 
                    </li>
                </ul>
                </li>
            </ul>
            ))
          }
        </div>
      </div>
    </div>
  );
};

export default CovidCarrierStatus;
